// --- CONFIGURACIÓN ---
const monthMap = {
    '01': 'Jan', '02': 'Feb', '03': 'Mar', '04': 'Apr', '05': 'May', '06': 'Jun',
    '07': 'Jul', '08': 'Aug', '09': 'Sep', '10': 'Oct', '11': 'Nov', '12': 'Dec'
};

const monthsShort = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

// 0. RECIBIR PARÁMETROS DEL FRONTEND
const inputParams = items[0].json || {};
const TARGET_PERIOD = inputParams.target_period || null; // Ej: "2025-05-01" (El mes a corregir)

// 1. CALCULAR FECHAS CLAVE (La Magia del Lag)
let DATA_PERIOD_FILTER = null; // La fecha de los datos que vamos a leer (Mes anterior)
let FORECAST_COL_NAME = null;  // La columna del forecast que vamos a buscar (Mes target)

if (TARGET_PERIOD) {
    // A. Calcular Mes de Datos (Target - 1 Mes)
    // Asumimos formato YYYY-MM-DD
    const parts = TARGET_PERIOD.split('-');
    const year = parseInt(parts[0]);
    const month = parseInt(parts[1]) - 1; // 0-11
    const day = parseInt(parts[2]);

    const targetDate = new Date(year, month, day);

    // Retrocedemos 1 mes para buscar los datos reales (Sellout/Precio del mes pasado)
    const dataDate = new Date(year, month, day);
    dataDate.setMonth(dataDate.getMonth() - 1);

    // Reconstruimos el string YYYY-MM-DD para filtrar el archivo 70k
    const prevYear = dataDate.getFullYear();
    const prevMonth = (dataDate.getMonth() + 1).toString().padStart(2, '0');
    const prevDay = dataDate.getDate().toString().padStart(2, '0');
    DATA_PERIOD_FILTER = `${prevYear}-${prevMonth}-${prevDay}`; // Ej: "2025-04-01"

    // B. Calcular Nombre de Columna del Forecast (El mes Target: "25-May")
    const targetYearShort = targetDate.getFullYear().toString().slice(2);
    const targetMonthName = monthsShort[targetDate.getMonth()];
    FORECAST_COL_NAME = `${targetYearShort}-${targetMonthName}`; // Ej: "25-May"

    // Debug (Puedes verlo en el output de n8n)
    console.log(`Target: ${TARGET_PERIOD} | Usando Datos de: ${DATA_PERIOD_FILTER} | Buscando Forecast: ${FORECAST_COL_NAME}`);
}

// --- 2. LECTURA Y PARSEO ---
const referenceMap = new Map(); 
let transactions = [];          

for (let i = 0; i < items.length; i++) {
    const item = items[i];

    // ARCHIVO "70k" (Datos Reales)
    if (item.binary && item.binary['70k']) {
        const buffer = await this.helpers.getBinaryDataBuffer(i, '70k');
        const text = buffer.toString('utf8');
        transactions = parseCSV(text, ','); 
    }

    // ARCHIVO "4k" (Forecasts)
    if (item.binary && item.binary['4k']) {
        const buffer = await this.helpers.getBinaryDataBuffer(i, '4k');
        const text = buffer.toString('utf8');
        const rows = parseCSV(text, ';');

        for (const row of rows) {
            const key = `${String(row.PRDID).trim()}_${String(row.CUSTID).trim()}`;
            referenceMap.set(key, row);
        }
    }
}

// --- 3. CRUCE INTELIGENTE ---
const results = [];

for (const txn of transactions) {
    // A. FILTRO DE DATOS: Buscamos solo las filas del MES ANTERIOR
    if (DATA_PERIOD_FILTER && txn.PERIODID3 !== DATA_PERIOD_FILTER) {
        continue; 
    }

    // Leemos los Drivers del mes pasado
    let p_index_str = txn['PINDEX'] || "1.0";
    let z_sellout_str = txn['ZSELLOUTCLIENTE'] || "0";

    const p_index = parseFloat(p_index_str.toString().replace(',', '.'));
    const z_sellout = parseFloat(z_sellout_str.toString().replace(',', '.'));

    // B. BÚSQUEDA DEL FORECAST: Buscamos el forecast del MES TARGET
    const key = `${String(txn.PRDID).trim()}_${String(txn.CUSTID).trim()}`;
    const refData = referenceMap.get(key);

    let foreActValue = null;
    let region = null;
    let group = null;

    if (refData) {
        region = refData['CUSTREGION'];
        group = refData['CUSTGROUP'];

        // Usamos directamente el nombre de columna calculado al inicio
        if (FORECAST_COL_NAME) {
            const val = refData[FORECAST_COL_NAME];
            if (val !== undefined && val !== null && val !== "") {
                foreActValue = parseFloat(val.toString().replace(',', '.'));
            }
        }
    }

    // --- 4. OUTPUT FINAL ---
    if (foreActValue !== null) {
        const mergedItem = { ...txn };

        mergedItem['CUSTREGION'] = region;
        mergedItem['CUSTGROUP'] = group;

        // AQUÍ ESTÁ LA CLAVE:
        // foreAct es del Futuro (Target)
        // PINDEX/ZSELLOUT son del Pasado (Datos recientes)
        mergedItem['foreAct'] = foreActValue; 
        mergedItem['PINDEX'] = p_index;
        mergedItem['ZSELLOUTCLIENTE'] = z_sellout;
        // AGREGAR ESTO: Pasamos la venta real del mes de los datos (Abril)
        mergedItem['LAST_MONTH_SALE'] = parseFloat(txn['ADJUSTEDACTUALSQTY'] || 0);
        // Metadata útil para entender qué pasó
        mergedItem['_meta_data_period'] = DATA_PERIOD_FILTER;
        mergedItem['_meta_forecast_col'] = FORECAST_COL_NAME;

        results.push({ json: mergedItem });
    }
}

return results;

// --- FUNCIONES DE PARSEO ---
function parseCSV(csvText, separator) {
    const lines = csvText.split('\n');
    if (lines.length < 2) return [];
    const headers = parseCSVLine(lines[0], separator).map(h => h.trim().replace(/^["']|["']$/g, ''));
    const parsedData = [];
    for (let i = 1; i < lines.length; i++) {
        const line = lines[i].trim();
        if (!line) continue;
        const values = parseCSVLine(line, separator);
        const obj = {};
        headers.forEach((header, index) => {
            let val = values[index] ? values[index].trim() : null;
            if (val) val = val.replace(/^["']|["']$/g, '');
            obj[header] = val;
        });
        parsedData.push(obj);
    }
    return parsedData;
}

function parseCSVLine(text, separator) {
    const escapedSep = separator === '.' ? '\\.' : separator;
    const re_value = new RegExp(`(?!\\s*$)\\s*(?:'([^']*)'|"([^"]*)"|([^${escapedSep}'"\\s\\\\]*(?:\\s+[^${escapedSep}'"\\s\\\\]+)*))\\s*(?:${escapedSep}|$)`, 'g');
    const a = [];
    text.replace(re_value, function(m0, m1, m2, m3) {
        if (m1 !== undefined) a.push(m1.replace(/\\'/g, "'"));
        else if (m2 !== undefined) a.push(m2.replace(/\\"/g, '"'));
        else if (m3 !== undefined) a.push(m3);
        return '';
    });
    if (new RegExp(`${escapedSep}\\s*$`).test(text)) a.push('');
    return a;
}

// --- CONFIGURACIÓN AVANZADA ---
const PARAMS = {
    ELASTICITY_PRICE: 1.5,      // Sensibilidad al precio
    STOCK_PRESSURE_FACTOR: 0.8, // Reacción ante quiebres de stock
    BIAS_CORRECTION: 1.0,       // Corrección total del error histórico
    MOMENTUM_FACTOR: 0.65       // Peso de la inercia del mes anterior
};

// --- 1. PRE-PROCESAMIENTO: AGREGACIÓN DE DUPLICADOS ---
// Unificamos filas que tengan mismo CUSTID y PRDID
const aggregatedItems = {};

for (const item of items) {
    const r = item.json;
    const key = `${r.CUSTID}_${r.PRDID}`;

    if (!aggregatedItems[key]) {
        // Inicializamos con el primer registro encontrado
        aggregatedItems[key] = { ...r };
        aggregatedItems[key]._count = 1;
        // Aseguramos números
        aggregatedItems[key].ZSELLOUTCLIENTE = parseFloat(r.ZSELLOUTCLIENTE || 0);
        aggregatedItems[key].PINDEX = parseFloat(r.PINDEX || 0);
        aggregatedItems[key].LAST_MONTH_SALE = parseFloat(r.LAST_MONTH_SALE || 0);
        aggregatedItems[key].foreAct = parseFloat(r.foreAct || 0);
    } else {
        // Si ya existe, acumulamos
        aggregatedItems[key]._count += 1;
        // Sumamos Sellout (Venta total de todas las tiendas)
        aggregatedItems[key].ZSELLOUTCLIENTE += parseFloat(r.ZSELLOUTCLIENTE || 0);
        // Sumamos Pindex para luego promediar
        aggregatedItems[key].PINDEX += parseFloat(r.PINDEX || 0);
    }
}

// Convertimos de vuelta a array y promediamos PINDEX
// AQUÍ SE DEFINE uniqueItems (Esta era la línea que faltaba)
const uniqueItems = Object.values(aggregatedItems).map(r => {
    if (r._count > 1) {
        r.PINDEX = r.PINDEX / r._count; // Promedio simple
    }
    return { json: r };
});

// --- 2. MODELO CORRECTOR BIDIRECCIONAL ---
return uniqueItems.map(item => {
    const r = item.json;

    // A. INPUTS
    const fcst_original = parseFloat(r.foreAct || 0);
    const sellout_units = parseFloat(r.ZSELLOUTCLIENTE || 0); 
    const p_index = parseFloat(r.PINDEX || 1.0);
    const bias_historico = parseFloat(r.BIAS || 0);
    const last_month_sale = parseFloat(r.LAST_MONTH_SALE || 0);
    const status = r.STATUS || 'STABLE';

    if (fcst_original <= 0) return item;

    // B. CAP DINÁMICO
    let MAX_CAP = 0.80;
    if (status === 'CRITICAL') MAX_CAP = 3.0;      
    if (status === 'OPPORTUNITY') MAX_CAP = 1.0;  
    if (status === 'CHAOS') MAX_CAP = 2.0;  

    // C. DRIVERS
    const price_delta = (1.0 - p_index) * PARAMS.ELASTICITY_PRICE;

    let sellout_delta = 0;
    if (sellout_units > 0) {
        const ratio = sellout_units / fcst_original;
        if (ratio > 1.10) {
            sellout_delta = (ratio - 1.0) * PARAMS.STOCK_PRESSURE_FACTOR;
        } else if (ratio < 0.30) {
            sellout_delta = (ratio - 1.0) * 0.5;
        }
    }

    // AJUSTE BIAS: Si el bias es positivo (sobre-pronóstico), reducimos. 
    // Pero limitamos el impacto del bias negativo para que no mate el forecast.
    let bias_delta = 0;
    if (Math.abs(bias_historico) > 0.05) {
        // Usamos una función para que bias extremos no den deltas de -800%
        // Si bias es 8, el delta será -0.8 (reducción fuerte pero controlada)
        bias_delta = -(bias_historico / (1 + Math.abs(bias_historico))) * PARAMS.BIAS_CORRECTION;
    }

    let momentum_delta = 0;
    if (last_month_sale > 0) {
        const momentum_ratio = last_month_sale / fcst_original;
        momentum_delta = (momentum_ratio - 1.0) * PARAMS.MOMENTUM_FACTOR;
    }

    // D. RESULTADO Y PROTECCIÓN DE NEGATIVOS
    const raw_delta = price_delta + sellout_delta + bias_delta + momentum_delta;

    // Aplicar Cap
    let capped_delta = raw_delta;
    if (capped_delta > MAX_CAP) capped_delta = MAX_CAP;
    if (capped_delta < -0.95) capped_delta = -0.95; // Nunca reducir más del 95%

    // Segunda capa de seguridad: el CAP por Status
    if (capped_delta < -MAX_CAP) capped_delta = -MAX_CAP;

    // CÁLCULO FINAL (Math.max asegura que el piso sea 0)
    const fcst_corrected = Math.max(0, Math.round(fcst_original * (1 + capped_delta)));
    const fcst_no_cap = Math.max(0, Math.round(fcst_original * (1 + raw_delta)));

    // Explicación
    const drivers = [];
    if (Math.abs(price_delta) > 0.01) drivers.push(`Precio ${(price_delta*100).toFixed(0)}%`);
    if (Math.abs(sellout_delta) > 0.01) drivers.push(`Sellout ${(sellout_delta*100).toFixed(0)}%`);
    if (Math.abs(bias_delta) > 0.01) drivers.push(`Bias ${(bias_delta*100).toFixed(0)}%`);
    if (Math.abs(momentum_delta) > 0.01) drivers.push(`Inercia ${(momentum_delta*100).toFixed(0)}%`);

    return {
        json: {
            ...r,
            MODEL_forecast_original: Math.round(fcst_original),
            MODEL_forecast_corrected: fcst_corrected,
            MODEL_forecast_no_cap: fcst_no_cap,
            MODEL_delta_pct: (capped_delta * 100).toFixed(1) + '%',
            MODEL_raw_delta_pct: (raw_delta * 100).toFixed(1) + '%',
            MODEL_drivers: drivers.join(', '),
            _debug_status: status
        }
    };
});

