// 1. Recuperamos los datos ORIGINALES del Loop (donde están CUSTID, Forecast, etc.)
// Asegúrate de que el nombre dentro de $('...') sea IDÉNTICO al nombre de tu nodo de loop.
const originalItems = $('Loop Over Items').all();

// 2. Recorremos las respuestas de la IA
return items.map((item, index) => {

    // 'aiData' es lo que escupe el nodo LLM (Solo el texto)
    const aiData = item.json;

    // 'originalData' es lo que teníamos antes de entrar a la IA (Los datos ricos)
    // Usamos el índice para casar la respuesta 1 con el dato original 1
    const originalData = originalItems[index].json;

    // Limpieza de la respuesta de texto
    const explicacion = (aiData.text || aiData.response || "").replace(/^["']|["']$/g, '').trim();

    // 3. Fusionamos todo en el JSON final
    return {
        json: {
            // Datos traídos del original
            "Periodo": originalData.PERIODID3,
            "ID_Cliente": originalData.CUSTID,
            "SKU": originalData.PRDID,
            "Grupo": originalData.CUSTGROUP,
            "Estatus": originalData._debug_status,

            // Métricas numéricas originales
            "Forecast_Original": parseInt(originalData.MODEL_forecast_original || 0),
            "Forecast_IA": parseInt(originalData.MODEL_forecast_corrected || 0),
            "Variacion": originalData.MODEL_delta_pct,
            "Drivers": originalData.MODEL_drivers,

            // La respuesta de la IA
            "Racional_IA": explicacion 
        }
    };
});
